const Form = () => {
    const [FullName,setFullName] = useState({
        fname: " ",
        lname:" ",
        email: " ",
    });
    
    
    const inputEvent=(event)=>{
        
    }

    const Fname=(event)=>{
        event.preventDefault();
        alert("Form Submitted")

    }


    return (
        <div>
        <form onSubmit={Fname}>
        <div className='input'>
            <h1>Hello {FullName.fname} {FullName.lname} </h1>
            <br/> {FullName.email}
            <input type="text" placeholder='Enter your First Name' name = "fName" onChange={inputEvent} required/>
            <br/>
            <input type="text" placeholder='Enter your Last Name' name = "lName" onChange={inputEvent} required/>
            <br/>
            <input type="email" placeholder='Enter your Email' name = "eMail" onChange={inputEvent}  required/>
            <button type="submit">click</button>
        </div>
        </form>
        </div>
    );
}

export default Form;
