import React,{useState} from 'react';


const Form = () => {
    const [FullName,setFullName] = useState({
        fname: " ",
        lname:" ",
    });
    
    
    const inputEvent=(event)=>{
        const value = event.target.value;
        const name = event.target.name;
        setFullName((preValue)=>{
            if(name === 'fName'){
                return{
                    fname:value,
                    lname:preValue.lname,
                };
            }else if(name === 'lName'){
                return{
                    fname:preValue.fname,
                    lname:value,
                };
            }
        })
    
    }

    const Fname=(event)=>{
        event.preventDefault();

    }


    return (
        <div>
        <form onSubmit={Fname}>
        <div className='input'>
            <h1>Hello {FullName.fnmae} {lastName.lname}</h1>
            <input type="text" placeholder='Enter your name' name = "fName" onChange={inputEvent} value={FullName.fname}/>
            <br/>
            <input type="text" placeholder='Enter your Password' name = "lName" onChange={inputEvent} value={FullName.lname}/>
            <button type="submit">click</button>
        </div>
        </form>
        </div>
    );
}

export default Form;
